        public bool Check(ref ValidationErrors errors, string id, int checkFlag, string checker)
        {
            try
            {
                MIS_Article entity = m_Rep.GetById(id);
                if (entity == null)
                {
                    errors.Add(Resource.Disable);
                    return false;
